package com.syntax.class01;

public class MyPrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// storing and representing numeric values;
		
		
		//whole numbers
		byte box1 = 127;
		short box2 = 32767;
		int box3 = 100000; //popular one
		long box4 = 100000045636L; //credit cards etc
		
		//decimals
		float variable1 = 2.99f;
		double variable2 = 99.99; //popular
		
		//storing single character
		
		char container='A';
		char dollar='$';
		
		//storing yes or no values
		boolean hot=true;
		boolean tired=false;
		
		System.out.println(variable2); //printing value
		System.out.println("variable2"); //printing text
		System.out.println(tired);
		System.out.println(hot);
		System.out.println(box1);
		System.out.println(box2);
		System.out.println(box3);
		System.out.println(box4);
		System.out.println(variable1);
		System.out.println(container);
		System.out.println(dollar);
		
//System.out.println(variable3); does not exist-ERROR
		
		
		
	}

}
